create table endereco (id integer generated by default as identity, bairro varchar(150) not null, cep varchar(255) not null, cidade varchar(150) not null, complemento varchar(255), logradouro varchar(150) not null, numero varchar(10) not null, status varchar(255) not null, uf varchar(2) not null, primary key (id));
create table estoque (id integer generated by default as identity, quantidade integer not null, status varchar(255) not null, medicamento_id integer not null, posto_id integer not null, primary key (id));
create table medicamento (id integer generated by default as identity, dosagem varchar(10) not null, fabricante varchar(50) not null, nome varchar(50) not null, status varchar(255) not null, primary key (id));
create table posto (id integer generated by default as identity, descricao varchar(150) not null, nome varchar(150) not null, status varchar(255) not null, endereco_id integer not null, primary key (id));
create table retirada (id integer generated by default as identity, data_cadastro timestamp(6), estoque_id integer not null, usuario_id integer not null, primary key (id));
create table usuario (id integer generated by default as identity, email varchar(100) not null, nome varchar(100) not null, senha varchar(200) not null, status varchar(255) not null, telefone varchar(15) not null, tipo_usuario varchar(255) not null, endereco_id integer not null, primary key (id));
